$(function() {
	$('.alert').alert();
	$('.btn').mouseover(function() {
		$(this).tooltip('show');
	});
	$('a').mouseover(function() {
		$(this).tooltip('show');
	});
	$('.print').click(function() {
		window.print();
	});
	$('#date').datepicker({
		format: 'dd-mm-yyyy'
	});

	$('a[data-toggle="tab"]').on('shown.bs.tab', function(e) {
		//save the latest tab using a cookie:
		$.cookie('ci_last_tab', $(e.target).attr('href'));
	});
	//activate latest tab, if it exists:
	var lastTab = $.cookie('ci_last_tab');
	if (lastTab) {
		$('a[href=' + lastTab + ']').tab('show');
	} else {
		// Set the first tab if cookie do not exist
		$('a[data-toggle="tab"]:first').tab('show');
	}

	if ($('#name')) {
		$("#name").autocomplete({
			source: "products/get_product_autocomplete" // path to the get_birds method
		});
	}
});